  const numPacked = items.filter((item) => item.packed === true).length;
  const packedPecentage = (numPacked / numItems).toFixed(2) * 100;
  return (
    <footer className="stats">
      {packedPecentage === 100 ? (
        <em>'You got everything! Ready to go!'</em>
