//1. create form element
//  - inside the form,use Array.from({length:},callBackFunctionInMapFunction)
//2. listening for the submit event that happens on the form use onSubmit={}
//  - the event will happen as soon as we click the button or hit enter while
//    we are in the input element

const initialItems = [
  { id: 1, description: "Passports", quantity: 2, packed: false },
  { id: 2, description: "Socks", quantity: 12, packed: false },
  { id: 2, description: "charger", quantity: 12, packed: true },
  { id: 2, description: "tooth brush", quantity: 12, packed: false },
  { id: 2, description: "wallet", quantity: 12, packed: false },
  { id: 2, description: "passport", quantity: 12, packed: true },
];
export default function App() {
  return (
    <div>
      <Logo />
      <Form />
      <PackingList />
      <Stats />
    </div>
  );
}
function Logo() {
  return <h1>üèù Far Away üß≥</h1>;
}

function handleSubmit() {}
function Form() {
  return (
    <form className="add-form" onSubmit={handleSubmit}>
      <h3>What do you need for your trip üòç ?</h3>
      <select>
        {Array.from({ length: 20 }, (_, i) => i + 1).map((num) => (
          <option value={num}>{num}</option>
        ))}
      </select>
      <input type="text" placeholder="Item..."></input>
      <button>Add</button>
    </form>
  );
}
function PackingList() {
  return (
    <div className="list">
      <ul>
        {initialItems.map((item) => (
          <Item item={item} key={item.id} />
        ))}
      </ul>
    </div>
  );
}
function Item({ item }) {
  return (
    <li className="list">
      <span style={item.packed ? { textDecoration: "line-through" } : {}}>
        <input type="checkbox" /> {item.quantity} {item.description}
        <button>‚ùå</button>
      </span>
    </li>
  );
}
function Stats() {
  return (
    <footer className="stats">
      üíº You have X items in your list, and you already packed X(X%).
    </footer>
  );
}
