  return (
    <div className="add-form">
      <h3>What do you need for your trip üòç ?</h3>
    </div>
  );
}
function PackingList() {
  return (
    <ul className="list">
      {initialItems.map((item) => (
        <li className="list">
          {item.id}.{item.description}
