import { useState } from "react";
import Item from "./Item.js";

export default function PackingList({
  items,
  onDeleteItems,
  onToggleItem,
  onClearAll,
}) {
  const packingListDiv = document.querySelector(".list");
  // console.log(packingListDiv);
  // console.log("cord:", packingListDiv.getBoundingClientRect());
  // const packingListViewPortHeight =
  //   packingListDiv.getBoundingClientRect().bottom;
  // console.log(packingListViewPortHeight);
  const [sortBy, setSortBy] = useState("input");
  let sortedItems;
  if (sortBy === "input") sortedItems = items;
  if (sortBy === "description")
    sortedItems = items
      .slice()
      .sort((a, b) => a.description.localeCompare(b.description));
  if (sortBy === "packed")
    sortedItems = items
      .slice()
      .sort((a, b) => Number(a.packed) - Number(b.packed));
  //Number(fasle) -> 0 ,Number(true) -> 1
  return (
    <div className="list" style={{ height: `${packingListViewPortHeight}px` }}>
      <ul>
        {sortedItems.map((item) => (
          <Item
            item={item}
            onDeleteItems={onDeleteItems}
            onToggleItem={onToggleItem}
            key={item.id}
          />
        ))}
      </ul>
      <div className="actions">
        <select value={sortBy} onChange={(e) => setSortBy(e.target.value)}>
          <option value="input">Sort by input</option>
          <option value="description">Sort by description</option>
          <option value="packed">Sort by packed</option>
        </select>
        <button onClick={onClearAll}>Clear list</button>
      </div>
    </div>
  );
}
